import React, { useEffect, useState } from "react";
import API from "../utils/axios";
import { useNavigate } from "react-router-dom";

const UserPage = () => {
  const [datetime, setDatetime] = useState("");
  const [headquarters, setHeadquarters] = useState("Kathmandu");
  const [address, setAddress] = useState("");
  const [hospital, setHospital] = useState("");
  const [doctor, setDoctor] = useState("");
  const [geoLocation, setGeoLocation] = useState({ lat: null, lon: null });
  const navigate = useNavigate();

  useEffect(() => {
    // Auto-fill date & time
    const now = new Date().toLocaleString();
    setDatetime(now);

    // Get geolocation
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          setGeoLocation({
            lat: pos.coords.latitude,
            lon: pos.coords.longitude,
          });
        },
        (err) => {
          console.error("Location error:", err);
        }
      );
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await API.post("/userlogs", {
        headquarters,
        address,
        hospital,
        doctor,
        geoLocation,
      });
      alert("Data saved successfully!");
      // Reset form
      setAddress("");
      setHospital("");
      setDoctor("");
      navigate("/"); // Redirect back to landing page after submit
    } catch (err) {
      alert(err.response?.data?.message || "Error saving data");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    alert("Logged out successfully!");
    navigate("/");
  };

  return (
    <div className="p-8">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">User Page</h1>
        <button
          onClick={handleLogout}
          className="bg-red-500 text-white px-4 py-2 rounded"
        >
          Logout
        </button>
      </div>

      {/* Form */}
      <form onSubmit={handleSubmit} className="space-y-4 max-w-lg">
        <div>
          <label className="block font-semibold">Date & Time</label>
          <input
            type="text"
            value={datetime}
            disabled
            className="border p-2 rounded w-full bg-gray-100"
          />
        </div>

        <div>
          <label className="block font-semibold">Headquarters</label>
          <select
            value={headquarters}
            onChange={(e) => setHeadquarters(e.target.value)}
            className="border p-2 rounded w-full"
          >
            {[
              "Kathmandu",
              "Biratnagar",
              "Janakpur",
              "Lahan",
              "Birgunj",
              "Pokhara",
              "Butwal",
              "Narayanghat",
            ].map((hq) => (
              <option key={hq} value={hq}>
                {hq}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block font-semibold">Address (Location)</label>
          <input
            type="text"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            className="border p-2 rounded w-full"
            placeholder="Enter address"
            required
          />
        </div>

        <div>
          <label className="block font-semibold">Hospital / Pharmacy</label>
          <input
            type="text"
            value={hospital}
            onChange={(e) => setHospital(e.target.value)}
            className="border p-2 rounded w-full"
            placeholder="Enter hospital/pharmacy"
            required
          />
        </div>

        <div>
          <label className="block font-semibold">Doctor's Name</label>
          <input
            type="text"
            value={doctor}
            onChange={(e) => setDoctor(e.target.value)}
            className="border p-2 rounded w-full"
            placeholder="Enter doctor's name"
            required
          />
        </div>

        <button
          type="submit"
          className="bg-blue-500 text-white px-6 py-2 rounded"
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default UserPage;
