import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const medicines = [
  { id:1, name:"Paracetamol", price:20, image:"/images/paracetamol.jpg", description:"Used for fever and mild pain relief." },
  { id:2, name:"Amoxicillin", price:50, image:"/images/amoxicillin.jpg", description:"Antibiotic to treat infections." },
  { id:3, name:"Cetirizine", price:30, image:"/images/cetirizine.jpg", description:"Relieves allergy symptoms." }
];

export default function MedicinePage() {
  const [selected, setSelected] = useState(null);
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const handleLogout = () => {
    localStorage.removeItem("token");
    alert("Logged out");
    navigate("/");
  };

  return (
    <div className="p-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Inderma Care</h1>
        <div>
          {!token ? (
            <>
              <Link to="/login" className="mr-4 text-blue-600">Login</Link>
              <Link to="/register" className="text-green-600">Register</Link>
            </>
          ) : (
            <button className="bg-red-500 text-white px-3 py-1 rounded" onClick={handleLogout}>Logout</button>
          )}
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {medicines.map(m => (
          <div key={m.id} className="border p-4 rounded shadow cursor-pointer" onClick={() => setSelected(m)}>
            <img src={m.image} alt={m.name} className="h-32 w-full object-cover mb-2" />
            <h2 className="text-xl font-semibold">{m.name}</h2>
            <p className="text-green-600 font-bold">Rs. {m.price}</p>
          </div>
        ))}
      </div>

      {selected && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40">
          <div className="bg-white p-6 rounded shadow-lg max-w-md w-full">
            <h2 className="text-2xl font-bold mb-2">{selected.name}</h2>
            <p className="mb-4">{selected.description}</p>
            <p className="text-green-600 font-bold">Price: Rs. {selected.price}</p>
            <div className="mt-4 flex justify-end">
              <button className="bg-red-500 text-white px-4 py-2 rounded" onClick={() => setSelected(null)}>Close</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
