import React, { useEffect, useState } from "react";
import API from "../utils/axios";
import { useNavigate } from "react-router-dom";

const AdminPage = () => {
  const [logs, setLogs] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchLogs();
  }, []);

  const fetchLogs = async () => {
    try {
      const res = await API.get("/userlogs/all");
      setLogs(res.data);
    } catch (err) {
      alert("Access denied or error fetching logs");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    alert("Logged out successfully!");
    navigate("/");
  };

  return (
    <div className="p-8">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Admin Dashboard</h1>
        <button
          onClick={handleLogout}
          className="bg-red-500 text-white px-4 py-2 rounded"
        >
          Logout
        </button>
      </div>

      {/* Logs Table */}
      <div className="overflow-x-auto">
        <table className="min-w-full border">
          <thead className="bg-gray-200">
            <tr>
              <th className="p-2 border">#</th>
              <th className="p-2 border">User</th>
              <th className="p-2 border">Email</th>
              <th className="p-2 border">Headquarters</th>
              <th className="p-2 border">Address</th>
              <th className="p-2 border">Hospital/Pharmacy</th>
              <th className="p-2 border">Doctor</th>
              <th className="p-2 border">Date & Time</th>
            </tr>
          </thead>
          <tbody>
            {logs.length > 0 ? (
              logs.map((log, index) => (
                <tr key={log._id} className="text-center">
                  <td className="p-2 border">{index + 1}</td>
                  <td className="p-2 border">{log.user?.name || "N/A"}</td>
                  <td className="p-2 border">{log.user?.email || "N/A"}</td>
                  <td className="p-2 border">{log.headquarters}</td>
                  <td className="p-2 border">{log.address}</td>
                  <td className="p-2 border">{log.hospital}</td>
                  <td className="p-2 border">{log.doctor}</td>
                  <td className="p-2 border">
                    {new Date(log.datetime).toLocaleString()}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="8" className="p-4 text-center text-gray-500">
                  No records found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminPage;
