import React, { useEffect, useState } from "react";
import API from "../utils/axios";

const AdminPage = () => {
  const [medicines, setMedicines] = useState([]);
  const [logs, setLogs] = useState([]);
  const [newMed, setNewMed] = useState({
    name: "",
    description: "",
    price: "",
    image: "",
  });

  // Fetch all medicines and logs
  useEffect(() => {
    fetchMedicines();
    fetchLogs();
  }, []);

  const fetchMedicines = async () => {
    const res = await API.get("/admin/medicines");
    setMedicines(res.data);
  };

  const fetchLogs = async () => {
    const res = await API.get("/admin/userlogs");
    setLogs(res.data);
  };

  const handleAddMedicine = async (e) => {
    e.preventDefault();
    await API.post("/admin/medicines", newMed);
    setNewMed({ name: "", description: "", price: "", image: "" });
    fetchMedicines();
  };

  const handleDeleteMedicine = async (id) => {
    await API.delete(`/admin/medicines/${id}`);
    fetchMedicines();
  };

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

      {/* Add Medicine */}
      <div className="mb-8">
        <h2 className="text-2xl font-semibold mb-2">Add New Medicine</h2>
        <form className="space-y-2" onSubmit={handleAddMedicine}>
          <input
            type="text"
            placeholder="Name"
            value={newMed.name}
            onChange={(e) => setNewMed({ ...newMed, name: e.target.value })}
            className="border p-2 rounded w-full"
            required
          />
          <input
            type="text"
            placeholder="Description"
            value={newMed.description}
            onChange={(e) => setNewMed({ ...newMed, description: e.target.value })}
            className="border p-2 rounded w-full"
          />
          <input
            type="number"
            placeholder="Price"
            value={newMed.price}
            onChange={(e) => setNewMed({ ...newMed, price: e.target.value })}
            className="border p-2 rounded w-full"
            required
          />
          <input
            type="text"
            placeholder="Image URL"
            value={newMed.image}
            onChange={(e) => setNewMed({ ...newMed, image: e.target.value })}
            className="border p-2 rounded w-full"
          />
          <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">
            Add Medicine
          </button>
        </form>
      </div>

      {/* Medicine List */}
      <div className="mb-8">
        <h2 className="text-2xl font-semibold mb-2">Medicine List</h2>
        <ul>
          {medicines.map((med) => (
            <li key={med._id} className="flex justify-between border p-2 mb-2 rounded">
              <span>{med.name} (Rs. {med.price})</span>
              <button
                className="bg-red-500 text-white px-2 py-1 rounded"
                onClick={() => handleDeleteMedicine(med._id)}
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>

      {/* Employee Logs */}
      <div>
        <h2 className="text-2xl font-semibold mb-2">Employee Logs</h2>
        <div className="overflow-auto max-h-96 border p-2 rounded">
          <table className="w-full table-auto">
            <thead>
              <tr>
                <th className="border px-2">User</th>
                <th className="border px-2">HQ</th>
                <th className="border px-2">Address</th>
                <th className="border px-2">Hospital</th>
                <th className="border px-2">Doctor</th>
                <th className="border px-2">Date/Time</th>
                <th className="border px-2">Location (lat/lon)</th>
              </tr>
            </thead>
            <tbody>
              {logs.map((log) => (
                <tr key={log._id}>
                  <td className="border px-2">{log.user?.name || "N/A"}</td>
                  <td className="border px-2">{log.headquarters}</td>
                  <td className="border px-2">{log.address}</td>
                  <td className="border px-2">{log.hospital}</td>
                  <td className="border px-2">{log.doctor}</td>
                  <td className="border px-2">{new Date(log.datetime).toLocaleString()}</td>
                  <td className="border px-2">{log.geoLocation?.lat}, {log.geoLocation?.lon}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminPage;
