import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

export default function Login() {
  const [email,setEmail]=useState("");
  const [password,setPassword]=useState("");
  const navigate = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", { email, password });
      localStorage.setItem("token", res.data.token);
      alert("Login successful");
      navigate("/user");
    } catch (err) {
      alert(err.response?.data?.message || "Login failed");
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md w-96">
        <h2 className="text-2xl mb-4">Login</h2>
        <form onSubmit={submit} className="space-y-3">
          <input required type="email" className="border p-2 w-full" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} />
          <input required type="password" className="border p-2 w-full" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} />
          <button className="bg-blue-500 text-white px-4 py-2 rounded w-full">Login</button>
        </form>
        <p className="mt-3 text-sm">No account? <Link to="/register" className="text-green-600">Register</Link></p>
      </div>
    </div>
  );
}
