import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";

const medicines = [
  {
    id: 1,
    name: "Paracetamol",
    price: 20,
    image: "/images/paracetamol.jpg",
    description: "Used for fever and mild pain relief.",
  },
  {
    id: 2,
    name: "Amoxicillin",
    price: 50,
    image: "/images/amoxicillin.jpg",
    description: "Antibiotic used to treat bacterial infections.",
  },
  {
    id: 3,
    name: "Cetirizine",
    price: 30,
    image: "/images/cetirizine.jpg",
    description: "Relieves allergy symptoms such as sneezing and itching.",
  },
];

const MedicinePage = () => {
  const [selectedMed, setSelectedMed] = useState(null);
  const [role, setRole] = useState(null);
  const [token, setToken] = useState(null);
  const navigate = useNavigate();

  // âœ… On first load, get token and role from localStorage safely
  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    const storedRole = localStorage.getItem("role");
    if (storedToken) setToken(storedToken);
    if (storedRole) setRole(storedRole);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    setToken(null);
    setRole(null);
    alert("Logged out successfully!");
    navigate("/");
  };

  return (
    <div className="p-8">
      {/* Navbar */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Inderma Care</h1>
        <div className="space-x-4">
          {!token ? (
            <>
              <Link to="/login" className="text-blue-600 font-semibold hover:underline">
                Login
              </Link>
              <Link to="/register" className="text-green-600 font-semibold hover:underline">
                Register
              </Link>
            </>
          ) : (
            <>
              {/* ðŸ‘‡ Admin only */}
              {role === "admin" && (
                <Link
                  to="/admin"
                  className="text-purple-600 font-semibold hover:underline"
                >
                  Admin Dashboard
                </Link>
              )}

              {/* ðŸ‘‡ Normal user only */}
              {(role === "user" || role === "employee") &&  (
                <Link
                  to="/user"
                  className="text-blue-600 font-semibold hover:underline"
                >
                  User Dashboard
                </Link>
              )}

              <button
                onClick={handleLogout}
                className="bg-red-500 text-white px-4 py-2 rounded"
              >
                Logout
              </button>
            </>
          )}
        </div>
      </div>

      {/* Medicine Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {medicines.map((med) => (
          <div
            key={med.id}
            className="border p-4 rounded shadow cursor-pointer hover:shadow-lg transition"
            onClick={() => setSelectedMed(med)}
          >
            <img
              src={med.image}
              alt={med.name}
              className="h-32 w-full object-cover mb-2 rounded"
            />
            <h2 className="text-xl font-semibold">{med.name}</h2>
            <p className="text-green-600 font-bold">Rs. {med.price}</p>
          </div>
        ))}
      </div>

      {/* Popup Modal */}
      {selectedMed && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg max-w-md w-full">
            <h2 className="text-2xl font-bold mb-2">{selectedMed.name}</h2>
            <p className="mb-4">{selectedMed.description}</p>
            <p className="text-green-600 font-bold">
              Price: Rs. {selectedMed.price}
            </p>
            <button
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded"
              onClick={() => setSelectedMed(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default MedicinePage;
