import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { jwtDecode } from "jwt-decode";

const medicines = [
  { id: 1, name: "Paracetamol", price: 20, image: "/images/paracetamol.jpg", description: "Used for fever and mild pain relief." },
  { id: 2, name: "Amoxicillin", price: 50, image: "/images/amoxicillin.jpg", description: "Antibiotic used to treat bacterial infections." },
  { id: 3, name: "Cetirizine", price: 30, image: "/images/cetirizine.jpg", description: "Relieves allergy symptoms such as sneezing and itching." },
];

const MedicinePage = () => {
  const [selectedMed, setSelectedMed] = useState(null);
  const [role, setRole] = useState("");
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setRole(decoded.role); // role: admin or employee
      } catch (err) {
        console.error("Invalid token");
      }
    }
  }, [token]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    alert("Logged out successfully!");
    navigate("/");
  };

  return (
    <div className="p-8">
      {/* Navbar */}
      // Inside MedicinePage component
const token = localStorage.getItem("token");
const userRole = localStorage.getItem("role"); // we'll store this at login

return (
  <div className="flex justify-between items-center mb-6">
    <h1 className="text-3xl font-bold">Inderma Care</h1>

    <div className="space-x-4">
      {!token && (
        <>
          <Link to="/login" className="text-blue-600">Login</Link>
          <Link to="/register" className="text-green-600">Register</Link>
        </>
      )}

      {/* ðŸ‘‡ Show only for normal logged-in users */}
      {token && userRole === "employee" && (
        <Link
          to="/user"
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          User Page
        </Link>
      )}

      {/* ðŸ‘‡ Show only for admins */}
      {token && userRole === "admin" && (
        <Link
          to="/admin"
          className="bg-gray-800 text-white px-4 py-2 rounded"
        >
          Admin Dashboard
        </Link>
      )}

      {token && (
        <button
          onClick={handleLogout}
          className="bg-red-500 text-white px-4 py-2 rounded"
        >
          Logout
        </button>
      )}
    </div>
  </div>
);


      {/* Medicines */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {medicines.map((med) => (
          <div
            key={med.id}
            className="border p-4 rounded shadow cursor-pointer"
            onClick={() => setSelectedMed(med)}
          >
            <img
              src={med.image}
              alt={med.name}
              className="h-32 w-full object-cover mb-2"
            />
            <h2 className="text-xl font-semibold">{med.name}</h2>
            <p className="text-green-600 font-bold">Rs. {med.price}</p>
          </div>
        ))}
      </div>

      {/* Popup Modal */}
      {selectedMed && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg max-w-md w-full">
            <h2 className="text-2xl font-bold mb-2">{selectedMed.name}</h2>
            <p className="mb-4">{selectedMed.description}</p>
            <p className="text-green-600 font-bold">
              Price: Rs. {selectedMed.price}
            </p>
            <button
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded"
              onClick={() => setSelectedMed(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default MedicinePage;
