import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const medicines = [
  { id: 1, name: "Paracetamol", price: 20, image: "/images/paracetamol.jpg", description: "Used for fever and mild pain relief." },
  { id: 2, name: "Amoxicillin", price: 50, image: "/images/amoxicillin.jpg", description: "Antibiotic used to treat infections." },
  { id: 3, name: "Cetirizine", price: 30, image: "/images/cetirizine.jpg", description: "Used to relieve allergy symptoms." }
];

const MedicinePage = () => {
  const [selectedMed, setSelectedMed] = useState(null);
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Navbar */}
      <nav className="bg-white shadow-md px-8 py-4 flex justify-between items-center">
        <h1 className="text-3xl font-bold text-blue-700">Inderma Care</h1>
        <div className="space-x-4">
          {!token ? (
            <>
              <Link to="/login" className="text-blue-600 font-semibold hover:underline">Login</Link>
              <Link to="/register" className="text-green-600 font-semibold hover:underline">Register</Link>
            </>
          ) : (
            <button
              onClick={handleLogout}
              className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded transition"
            >
              Logout
            </button>
          )}
        </div>
      </nav>

      {/* Medicines */}
      <div className="p-8 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {medicines.map((med) => (
          <div
            key={med.id}
            className="bg-white rounded shadow-md p-4 hover:shadow-xl transition cursor-pointer"
            onClick={() => setSelectedMed(med)}
          >
            <img src={med.image} alt={med.name} className="h-40 w-full object-cover rounded mb-4" />
            <h2 className="text-xl font-semibold">{med.name}</h2>
            <p className="text-green-600 font-bold">Rs. {med.price}</p>
          </div>
        ))}
      </div>

      {/* Modal */}
      {selectedMed && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-6 rounded shadow-lg max-w-md w-full animate-slideIn">
            <h2 className="text-2xl font-bold mb-2">{selectedMed.name}</h2>
            <p className="mb-4">{selectedMed.description}</p>
            <p className="text-green-600 font-bold">Price: Rs. {selectedMed.price}</p>
            <button
              className="mt-4 bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded"
              onClick={() => setSelectedMed(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default MedicinePage;
